var e = workflowContext.actions.Parse_endpoints_array.outputs.body;

function sensorReadings(r) {
	return r.columns.map(c => {
		if (c.sensor && c.sensor.name) {
			return { name: [c.sensor.name, c.name].join(' - '), value: c.values.join(' ') };
		} else {
			return { name: c.name, value: c.values.join(' ') };
		}
	});
}

function flatten(obj, pk = '') {
	if (pk !== '') pk += ' ';
	let flat = {};
	Object.keys(obj).forEach((key) => {
		if (key === 'sensorReadings') {
			sensorReadings(obj[key]).forEach((r) => {
				flat[r.name] = r.value;
			});
		} else {
			if (typeof obj[key] === 'object' && obj[key] !== null) {
				Object.assign(flat, flatten(obj[key], pk + key));
			} else {
				flat[pk + key] = obj[key];
			}
		}
	});
	return flat;
}

return e.map(function(e) { return flatten(e.node); });

