query ($incidentHosts: EndpointFieldFilter, $endCursor: Cursor, $source: EndpointSource) {
  endpoints(
    source: $source
    first: 10
    after: $endCursor
    filter: $incidentHosts
  ) {
    edges {
      node {
        id
        name
        ipAddress
        os { platform generation }
        disks {
          name
          free
        }
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}
